sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;
	var selectElement = document.getElementById('assignedDoctor');
	var doctorNames = c.data.doctorNames;
	var sysIds = c.data.sysIds;

	for(var i=0; i<doctorNames.length; i++) {
		var option = document.createElement("option");
		//console.log(typeof sysIds[i]);

		option.value = sysIds[i];
		option.text = doctorNames[i];
		selectElement.add(option);
	}

	//document.getElementById('input').value = c.data.currentPatientName;
	//console.log(data.currentPatientName);
	//document.getElementById('assignedDoctor').value = c.data.currentPatientDoctor;

	$scope.onUpdate = function() {
		var name = document.getElementById('input').value;
		var doctor = document.getElementById('assignedDoctor').value;
		console.log(name);
		console.log(doctor);
		//c.data.name = c.name;
		//c.data.doctor = c.doctor;
		//c.server.update();
	}
};

