sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.doctorNames = [];
	data.sysIds = [];

	var doctorRecords = new GlideRecord('x_1299926_hospital_doctor_table');
	doctorRecords.query();

	while(doctorRecords.next()) {
		data.doctorNames.push(doctorRecords.getValue('doctor_name'));
		data.sysIds.push(doctorRecords.getValue('sys_id'));
	}

	var currentPatientRecord = new GlideRecord('x_1299926_hospital_patient_table');
	var currentSysId = $sp.getParameter('recordSysId');
	currentPatientRecord.addQuery('sys_id',currentSysId);
	currentPatientRecord.query();

	if(currentPatientRecord.next()) {
		data.currentPatientName = currentPatientRecord.getValue('patient_name');
		data.currentPatientDoctor = currentPatientRecord.getValue('u_reference_1');
		//console.log(data.currentPatientDoctor);
	}


	if(input) {
		/*
			var patientRecord = new GlideRecord('x_1299926_hospital_patient_table');
			patientRecord.initialize();
			patientRecord.setValue('patient_name',input.name);
			patientRecord.setValue('u_reference_1',input.doctor);
			patientRecord.insert();
		*/
	}



})();
